import React, { useState } from "react";

const SECRET_KEY = "supersecretkey"; // same as backend

async function hashHMAC(message, key) {
  const enc = new TextEncoder();
  const keyData = enc.encode(key);
  const msgData = enc.encode(message);

  const cryptoKey = await crypto.subtle.importKey(
    "raw", keyData, { name: "HMAC", hash: "SHA-256" }, false, ["sign"]
  );
  const sig = await crypto.subtle.sign("HMAC", cryptoKey, msgData);
  return Array.from(new Uint8Array(sig)).map(b => b.toString(16).padStart(2, '0')).join('');
}

function App() {
  const [results, setResults] = useState([]);

  const runDetection = async () => {
    const csvFile = "../data/gps_data.csv"; // adjust path if needed
    const signature = await hashHMAC(csvFile, SECRET_KEY);

    const res = await fetch("http://localhost:5000/detect", {
      method: "POST",
      headers: {"Content-Type": "application/json"},
      body: JSON.stringify({ csv_file: csvFile, signature })
    });

    if (!res.ok) {
      alert("Unauthorized or server error!");
      return;
    }

    const data = await res.json();
    setResults(data);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>GeoShield Plane GPS Spoof Detection</h1>
      <button onClick={runDetection}>Run Detection</button>
      <pre style={{ maxHeight: "500px", overflowY: "scroll" }}>
        {JSON.stringify(results, null, 2)}
      </pre>
    </div>
  );
}

export default App;
