import React, { useState, useEffect, useRef } from "react";
import { MapContainer, TileLayer, Marker, Tooltip, Polyline, LayersControl } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "./App.css";

const { BaseLayer } = LayersControl;
const indiaCenter = [22.0, 79.0];

const planeIcon = new L.Icon({
  iconUrl: process.env.PUBLIC_URL + "/plane.png",
  iconSize: [32, 32],
  iconAnchor: [16, 16]
});

const alertIcon = new L.Icon({
  iconUrl: process.env.PUBLIC_URL + "/alert.png",
  iconSize: [24, 24],
  iconAnchor: [12, 12]
});

function Sidebar({ planes }) {
  return (
    <div className="sidebar">
      <h3>Planes</h3>
      {Object.keys(planes).map(plane => (
        <div key={plane}>
          <strong>{plane}</strong> ({planes[plane].status})
        </div>
      ))}
    </div>
  );
}

function App() {
  const [planes, setPlanes] = useState({});
  const [trajectories, setTrajectories] = useState({});
  const [simulationStarted, setSimulationStarted] = useState(false);
  const audioRef = useRef(new Audio("https://actions.google.com/sounds/v1/alarms/beep_short.ogg"));

  // Fetch trajectories once at start
  const startSimulation = async () => {
    const csvFiles = ["gps_data.csv", "sample_real.csv", "sample_spoofed.csv"];
    const signature = await hashHMAC(csvFiles.join(","), "supersecretkey");

    const res = await fetch("http://localhost:5000/live_planes_multi", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ csv_files: csvFiles, signature })
    });
    const data = await res.json();

    // Save trajectories (list of points for each plane)
    const trajs = {};
    const startPositions = {};
    Object.keys(data).forEach(plane => {
      trajs[plane] = data[plane].predicted.concat([data[plane].current]); // for now: backend gives last points
      startPositions[plane] = {
        lat: trajs[plane][0].lat,
        lon: trajs[plane][0].lon,
        status: trajs[plane][0].status
      };
    });

    setPlanes(startPositions);
    setTrajectories(trajs);
    setSimulationStarted(true);
  };

  // Step along trajectory
  useEffect(() => {
    if (!simulationStarted) return;
    const intervals = {};

    Object.keys(trajectories).forEach(plane => {
      let index = 0;
      intervals[plane] = setInterval(() => {
        setPlanes(prev => {
          if (index < trajectories[plane].length) {
            return {
              ...prev,
              [plane]: {
                ...prev[plane],
                lat: trajectories[plane][index].lat,
                lon: trajectories[plane][index].lon,
                status: trajectories[plane][index].status
              }
            };
          }
          return prev;
        });
        index++;
      }, 1000);
    });

    return () => {
      Object.values(intervals).forEach(clearInterval);
    };
  }, [simulationStarted, trajectories]);

  // Drag spoof detection
  const handleDragEnd = (plane, e) => {
    const pos = e.target.getLatLng();
    const nearest = trajectories[plane] || [];
    let minDist = Infinity;
    nearest.forEach(p => {
      const d = L.latLng(pos).distanceTo(L.latLng([p.lat, p.lon]));
      if (d < minDist) minDist = d;
    });

    if (minDist > 50000) { // 50km off
      setPlanes(prev => ({
        ...prev,
        [plane]: { ...prev[plane], lat: pos.lat, lon: pos.lng, status: "SPOOFED" }
      }));
      alert(`${plane} has gone off trajectory! Spoof attack detected.`);
      audioRef.current.play();
    }
  };

  return (
    <div style={{ display: "flex" }}>
      <Sidebar planes={planes} />
      <div style={{ width: "88%", position: "relative" }}>
        {!simulationStarted && (
          <button
            onClick={startSimulation}
            style={{
              position: "absolute",
              top: "20px",
              left: "50%",
              transform: "translateX(-50%)",
              zIndex: 1000,
              padding: "10px 20px",
              fontSize: "16px",
              cursor: "pointer"
            }}
          >
            Start Simulation
          </button>
        )}

        <MapContainer center={indiaCenter} zoom={5} style={{ height: "100vh", width: "100%" }}>
          <LayersControl position="topright">
            <BaseLayer checked name="OpenStreetMap">
              <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
            </BaseLayer>
            <BaseLayer name="ESRI Satellite">
              <TileLayer url="https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}" />
            </BaseLayer>
          </LayersControl>

          {Object.keys(planes).map(plane => {
            const pos = planes[plane];
            return (
              <React.Fragment key={plane}>
                <Polyline positions={(trajectories[plane] || []).map(p => [p.lat, p.lon])} color="blue" />
                <Marker
                  position={[pos.lat, pos.lon]}
                  icon={planeIcon}
                  draggable={true}
                  eventHandlers={{ dragend: (e) => handleDragEnd(plane, e) }}
                >
                  <Tooltip direction="top" offset={[0, -20]} permanent>
                    <div style={{ color: pos.status === "SAFE" ? "green" : "red", fontWeight: "bold" }}>
                      {plane}: {pos.status}
                    </div>
                  </Tooltip>
                </Marker>
                {pos.status === "SPOOFED" && (
                  <Marker position={[pos.lat + 0.05, pos.lon]} icon={alertIcon} interactive={false} />
                )}
              </React.Fragment>
            );
          })}
        </MapContainer>
      </div>
    </div>
  );
}

// HMAC helper
async function hashHMAC(message, key) {
  const enc = new TextEncoder();
  const keyData = enc.encode(key);
  const msgData = enc.encode(message);
  const cryptoKey = await crypto.subtle.importKey(
    "raw",
    keyData,
    { name: "HMAC", hash: "SHA-256" },
    false,
    ["sign"]
  );
  const sig = await crypto.subtle.sign("HMAC", cryptoKey, msgData);
  return Array.from(new Uint8Array(sig)).map(b => b.toString(16).padStart(2, '0')).join('');
}

export default App;
